
public class Ifcondition {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 100;
		int b = 20;
		int c=40;
		if (a > b && a>c) 
		{
			System.out.println("a is greater");
		}
		else if(b>a && b>c )
		{
			System.out.println("b is greater");
		}
		else
		{
			System.out.println("c is greater");
		}
	}

}
